import "core-js/modules/es.array.push.js";
export default {
  data() {
    return {
      email: '',
      password: '',
      userType: 'admin',
      userTypeOptions: [{
        value: 'admin',
        text: 'Administrateur'
      }, {
        value: 'teacher',
        text: 'Enseignant'
      }, {
        value: 'student',
        text: 'Etudiant'
      }]
    };
  },
  methods: {
    async submitForm() {
      const formData = {
        email: this.email,
        password: this.password,
        userType: this.userType
      };
      const requestOptions = {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      };
      const serverUrl = 'http://127.0.0.1:8000/authentification/form_submission/';
      try {
        const response = await fetch(serverUrl, requestOptions);
        if (!response.ok) {
          throw new Error('Erreur lors de la requête.');
        }
        const data = await response.json();
        switch (formData.userType) {
          case 'admin':
            if (data.AuthAdmin === "OK") {
              this.$router.push('/admin');
            } else {
              alert('Authentification administrateur échouée');
            }
            break;
          case 'teacher':
            if (data.AuthTeacher === "OK") {
              this.$router.push('/teacher');
            } else {
              alert('Authentification enseignant échouée');
            }
            break;
          case 'student':
            if (data.AuthStudent === "OK") {
              this.$router.push('/student');
            } else {
              alert('Authentification étudiant échouée');
            }
            break;
        }
      } catch (error) {
        console.error('Erreur:', error);
        alert('Une erreur est survenue lors de la connexion.');
      }
    }
  }
};
