import { BForm, BTable } from 'bootstrap-vue';
import axios from 'axios';
export default {
  name: 'planningComponent',
  components: {
    BForm,
    BTable
  },
  data() {
    return {
      planning: [],
      fields: [{
        key: 'theme',
        label: 'Thème de Soutenance'
      }, {
        key: 'date',
        label: 'Date'
      }, {
        key: 'local',
        label: 'Local'
      }, {
        key: 'binome',
        label: 'Binôme'
      }, {
        key: 'encadreur',
        label: 'Encadreur'
      }, {
        key: 'jury',
        label: 'Jury'
      }]
    };
  },
  mounted() {
    this.fetchPlanning();
  },
  methods: {
    async fetchPlanning() {
      try {
        const response = await axios.get('http://127.0.0.1:8000/planning/get_planning/');
        if (response.data.success) {
          this.planning = response.data.data;
        } else {
          console.error('La récupération des données a échoué:', response.data.error);
        }
      } catch (error) {
        console.error('Erreur lors de la récupération des données:', error);
      }
    }
  }
};
